import{_ as d,c as a,o as r,ah as l}from"./chunks/framework.DaDXF32t.js";const e="/assets/anti-harmony.YboJY51q.png",i="/assets/lesson_affection_example._akCxSEB.png",u=JSON.parse('{"title":"如何使用","description":"","frontmatter":{},"headers":[],"relativePath":"usage_doc/config.md","filePath":"usage_doc/config.md","lastUpdated":1751942404000}'),o={name:"usage_doc/config.md"};function h(s,t,n,c,b,g){return r(),a("div",null,t[0]||(t[0]=[l('<h1 id="如何使用" tabindex="-1">如何使用 <a class="header-anchor" href="#如何使用" aria-label="Permalink to “如何使用”">​</a></h1><h2 id="一些关键的设置" tabindex="-1">一些关键的设置 <a class="header-anchor" href="#一些关键的设置" aria-label="Permalink to “一些关键的设置”">​</a></h2><h3 id="模拟器设置" tabindex="-1">模拟器设置 <a class="header-anchor" href="#模拟器设置" aria-label="Permalink to “模拟器设置”">​</a></h3><ul><li>分辨率: 我们极力推荐你使用<strong>1280x720</strong>尺寸</li></ul><h3 id="游戏内设置" tabindex="-1">游戏内设置 <a class="header-anchor" href="#游戏内设置" aria-label="Permalink to “游戏内设置”">​</a></h3><ul><li>记忆大厅：请勿选取亚子，爱露, 若藻等角色, 推荐<strong>关闭记忆大厅</strong></li><li>国际服语言：支持<strong>英文/繁体中文/韩文</strong></li><li>游戏内设置</li></ul><table tabindex="0"><thead><tr><th>名称</th><th>选项</th></tr></thead><tbody><tr><td>分辨率</td><td>60</td></tr><tr><td>加速渲染模式</td><td>兼容</td></tr><tr><td>后期处理</td><td>ON</td></tr><tr><td>抗锯齿</td><td>ON</td></tr><tr><td>战斗画面上下黑边</td><td><strong>OFF</strong></td></tr></tbody></table><h3 id="汉化补丁" tabindex="-1">汉化补丁 <a class="header-anchor" href="#汉化补丁" aria-label="Permalink to “汉化补丁”">​</a></h3><div class="warning custom-block"><p class="custom-block-title custom-block-title-default">WARNING</p><p>游戏汉化修改游戏中图像, 导致<strong>BAAS</strong>无法进行模版匹配</p><p><strong>请勿使用任何形式的汉化补丁</strong></p></div><h3 id="反和谐" tabindex="-1">反和谐 <a class="header-anchor" href="#反和谐" aria-label="Permalink to “反和谐”">​</a></h3><ol><li>反和谐仅在国服存在, 反和谐前后一些角色的立绘, 以及一些文字内容会有所不同</li><li><strong>BAAS</strong>在反和谐前 / 后均可正常使用</li><li><strong>BAAS</strong>支持一键反和谐, 仅需打开模拟器后点击UI中的一键反和谐按钮即可 <ul><li><img src="'+e+'" alt="anti-harmony"></li></ul></li></ol><h2 id="脚本参数填写帮助" tabindex="-1">脚本参数填写帮助 <a class="header-anchor" href="#脚本参数填写帮助" aria-label="Permalink to “脚本参数填写帮助”">​</a></h2><h3 id="咖啡厅" tabindex="-1">咖啡厅 <a class="header-anchor" href="#咖啡厅" aria-label="Permalink to “咖啡厅”">​</a></h3><h4 id="如何设置咖啡厅邀请券为指定角色" tabindex="-1">如何设置咖啡厅邀请券为指定角色 <a class="header-anchor" href="#如何设置咖啡厅邀请券为指定角色" aria-label="Permalink to “如何设置咖啡厅邀请券为指定角色”">​</a></h4><ol><li>选择邀请模式为<code>邀请收藏的学生</code>, 序号为<code>1</code></li><li>收藏且仅收藏指定角色 <strong>related issue</strong>: <a href="https://github.com/pur1fying/blue_archive_auto_script/issues/211" target="_blank" rel="noreferrer">#211</a>, <a href="https://github.com/pur1fying/blue_archive_auto_script/issues/148" target="_blank" rel="noreferrer">#148</a></li></ol><h4 id="如何设置baas咖啡厅一日摸8次指定角色" tabindex="-1">如何设置BAAS咖啡厅一日摸8次指定角色 <a class="header-anchor" href="#如何设置baas咖啡厅一日摸8次指定角色" aria-label="Permalink to “如何设置BAAS咖啡厅一日摸8次指定角色”">​</a></h4><ol><li>首先按照上一级标题所述, 设置1号和2号咖啡厅邀请券为该角色</li><li><strong>关闭</strong>咖啡厅设置中的<strong>使用邀请券</strong></li><li><strong>开启</strong>调度中的<strong>1号咖啡厅邀请</strong> 和 <strong>2号咖啡厅邀请</strong></li></ol><p><strong>note</strong>:</p><ol><li><p>最终效果为BAAS将在以下时间摸头(国服为例):</p><ul><li>4:00(使用1号咖啡厅邀请券)</li><li>7:00</li><li>10:00</li><li>13:00</li><li>16:00(使用2号咖啡厅邀请券)</li><li>19:00</li><li>22:00</li><li>1:00</li></ul></li><li><p>相关issue: <a href="https://github.com/pur1fying/blue_archive_auto_script/issues/252" target="_blank" rel="noreferrer">#252</a></p></li></ol><h3 id="日程" tabindex="-1">日程 <a class="header-anchor" href="#日程" aria-label="Permalink to “日程”">​</a></h3><h4 id="如何设置baas做指定角色日程" tabindex="-1">如何设置BAAS做指定角色日程 <a class="header-anchor" href="#如何设置baas做指定角色日程" aria-label="Permalink to “如何设置BAAS做指定角色日程”">​</a></h4><ol><li>开启选项<code>优先做指定角色存在的日程</code></li><li>在 <code>src/images/你的服务器/lesson_affection</code> 目录下检索对应学生的模板图像, 复制文件名填入<code>指定角色</code>配置</li><li>如果没有收录你想要的角色的模板, 你需要向开发者提供以下截图 <ol><li>模拟器1280x720分辨率</li><li>图像是无损的(png)</li><li>日程界面中有指定角色</li><li>需要手动滑向上滑一下界面, 使图像包含九个区域角色头像</li><li>等待开发者适配</li></ol><ul><li>例(假设提供星野的模板): <img src="'+i+'" alt="image"></li></ul></li></ol><h3 id="制造" tabindex="-1">制造 <a class="header-anchor" href="#制造" aria-label="Permalink to “制造”">​</a></h3><h4 id="如何设置baas制造指定角色喜欢的礼物" tabindex="-1">如何设置BAAS制造指定角色喜欢的礼物 <a class="header-anchor" href="#如何设置baas制造指定角色喜欢的礼物" aria-label="Permalink to “如何设置BAAS制造指定角色喜欢的礼物”">​</a></h4><ol><li>制造级数至少为2级(只有二级可以指定)</li><li>在二级制造配置中<code>一键设置推荐优先级</code>选择该角色</li></ol><h3 id="主线剧情" tabindex="-1">主线剧情 <a class="header-anchor" href="#主线剧情" aria-label="Permalink to “主线剧情”">​</a></h3><ul><li>自动推剧情可以完成所有<strong>走格子</strong> 但是最终章一些战斗无法自动打</li><li>数字与章节对应表:</li></ul><table tabindex="0"><thead><tr><th>数字</th><th>对应章节</th></tr></thead><tbody><tr><td>1</td><td>序章（仅日服有）</td></tr><tr><td>2</td><td>第一章</td></tr><tr><td>3</td><td>第二章</td></tr><tr><td>4</td><td>第三章</td></tr><tr><td>5</td><td>第四章</td></tr><tr><td>6</td><td>最终章</td></tr><tr><td>7</td><td>第五章</td></tr><tr><td>8</td><td>EX章节</td></tr></tbody></table><p>填写: 用&quot;,&quot;隔开数字表示依次要推的章节</p><ul><li><strong>example:</strong></li></ul><table tabindex="0"><thead><tr><th>填写参数</th><th>BAAS执行</th></tr></thead><tbody><tr><td>1,3,6</td><td>依次推第一章，第三章，最终章</td></tr></tbody></table><ul><li><strong>note:</strong> 什么都不填会用默认配置</li></ul><table tabindex="0"><thead><tr><th>服务器</th><th>默认配置</th></tr></thead><tbody><tr><td>国服</td><td>2,3,4,5,6</td></tr><tr><td>国际服</td><td>2,3,4,5,6,7</td></tr><tr><td>日服</td><td>1,2,3,4,5,6,7,8）</td></tr></tbody></table><h3 id="普通图自动推图" tabindex="-1">普通图自动推图 <a class="header-anchor" href="#普通图自动推图" aria-label="Permalink to “普通图自动推图”">​</a></h3><ul><li>使用说明:</li></ul><ol><li>必须解锁自动结束回合和自动战斗</li><li>支持的关卡: 区域4 至 最新区域</li><li>每次游戏更新新区域时<strong>BAAS</strong>会在当天更新最新区域的自动推图</li><li><strong>BAAS</strong>推图时队伍移动点击坐标和路线是固定的，且所有移动只会点击一次，由于碧蓝档案游戏推图时若有多支队伍，则会根据队伍在游戏内编号大小坐标和移动次序不同。</li><li>由于4，自动推图时必须保证选择队的伍编号由小到大 。</li><li><a href="#小春版配队指南"><strong>小春版配队指南</strong></a></li><li><strong>BAAS会</strong>根据<strong>普通图推图设置</strong>中<a href="#各图所需属性">队伍属性配置</a>和<a href="#选队逻辑">选队逻辑</a>寻找合适配置进行推图</li></ol><h4 id="选队逻辑" tabindex="-1">选队逻辑: <a class="header-anchor" href="#选队逻辑" aria-label="Permalink to “选队逻辑:”">​</a></h4><ol><li>当选队逻辑选项为 <code>&quot;预设编队&quot;</code>时: <ul><li><strong>BAAS</strong>会根据需要属性的预设队伍进行选队, 你需要设置游戏中五行四列的预设是<strong>爆发, 贯穿, 神秘, 振动, 未使用</strong>中的哪一种</li><li>你需要人为保证所选择的队伍不会有角色冲突</li><li>当所需属性队伍数量不够时, 会按克制顺序依次尝试其他队伍 <strong>note</strong>: 此模式下不可以修改预设名称</li></ul></li><li>当选队伍逻辑选项为 <code>&quot;按侧栏属性编队&quot;</code>时: <ul><li>按照需要属性由小到大选择队伍</li><li>优先保证boss队的克制属性, 按克制顺序依次尝试队伍</li></ul></li><li>当选队伍逻辑选项为<code>&quot;按侧栏顺序编队&quot;</code>, 依次选择侧栏1, 2, 3, 4队</li></ol><h4 id="小春版配队指南" tabindex="-1">小春版配队指南 <a class="header-anchor" href="#小春版配队指南" aria-label="Permalink to “小春版配队指南”">​</a></h4><p>将选队逻辑选项选择<code>&quot;按侧栏顺序编队&quot;</code>, 按照<a href="#各图所需属性">队伍属性配置</a>设置游戏内队伍</p><p>例:</p><table tabindex="0"><thead><tr><th>关卡</th><th>游戏内配队</th></tr></thead><tbody><tr><td>普通图20图</td><td>队伍1配为爆发, 队伍2为贯穿</td></tr><tr><td>困难图20图</td><td>队伍1配为爆发, 队伍2为贯穿, 队伍3为爆发</td></tr></tbody></table><h3 id="普通图推图关卡填写说明" tabindex="-1">普通图推图关卡填写说明 <a class="header-anchor" href="#普通图推图关卡填写说明" aria-label="Permalink to “普通图推图关卡填写说明”">​</a></h3><ol><li><p>输入一个数字表示推该区域所有关卡一次</p></li><li><p>输入 数字-数字表示指定关卡</p></li><li><p><strong>BAAS</strong> 会自动检测关卡的<strong>sss</strong>, <strong>完成挑战任务</strong>情况, 跳过已经完成的关卡, 不会有额外体力消耗</p></li><li><p>例:</p><table tabindex="0"><thead><tr><th>填写</th><th>BAAS</th></tr></thead><tbody><tr><td>15,16-3</td><td>依次打15-1,15-2,15-3,15-4,15-5,16-3</td></tr></tbody></table></li></ol><p><strong>note:</strong></p><ol><li><a href="#各图所需属性">各区域所需属性</a></li></ol><h3 id="普通图扫荡填写说明" tabindex="-1">普通图扫荡填写说明 <a class="header-anchor" href="#普通图扫荡填写说明" aria-label="Permalink to “普通图扫荡填写说明”">​</a></h3><ol><li><p>每条扫荡配置形如 <code>region</code>-<code>task number</code>-<code>sweep times</code></p><table tabindex="0"><thead><tr><th>名称</th><th>含义</th><th>限制</th></tr></thead><tbody><tr><td>region</td><td>区域</td><td>4-最新章节</td></tr><tr><td>task number</td><td>关卡</td><td>1-5</td></tr><tr><td>sweep times</td><td>扫荡次数</td><td>正整数/max</td></tr></tbody></table></li><li><p>每条配置都用<code>&quot;,&quot;</code> 隔开</p></li><li><p>可用扫荡关卡: 教程-1与5图后 所有地图</p></li><li><p>特殊说明</p></li></ol><ul><li><code>sweep times</code> 可以为 <code>max</code></li><li>BAAS会根据当前体力和关卡体力计算当前关卡能否扫荡，不足或扫荡完次数为<code>max</code>的关卡会直接退出</li></ul><p>例:</p><table tabindex="0"><thead><tr><th>填写</th><th>BAAS</th></tr></thead><tbody><tr><td>15-3-3,20-3-max</td><td>然后扫荡15-3,3次,然后使用所有体力扫荡20-3</td></tr></tbody></table><h3 id="困难图推图关卡填写说明" tabindex="-1">困难图推图关卡填写说明 <a class="header-anchor" href="#困难图推图关卡填写说明" aria-label="Permalink to “困难图推图关卡填写说明”">​</a></h3><ol><li><p>格式同<a href="#普通图推图关卡填写说明">普通图推图关卡填写说明</a></p></li><li><p><strong>BAAS</strong>会自动检测关卡的 <strong>sss</strong>, <strong>领取礼物</strong>, <strong>完成挑战任务</strong>情况, 跳过已经完成的关卡,不会有额外体力消耗</p><table tabindex="0"><thead><tr><th>填写</th><th>BAAS</th></tr></thead><tbody><tr><td>15,16-3</td><td>依次完成H15-1,H15-2,H15-3,H16-3</td></tr></tbody></table></li></ol><p><strong>note</strong>:</p><ol><li>一些困难图需要三支队伍, 第三支队伍的属性均与该区域所需属性的第一个属性相同</li></ol><h3 id="困难图扫荡填写说明" tabindex="-1">困难图扫荡填写说明 <a class="header-anchor" href="#困难图扫荡填写说明" aria-label="Permalink to “困难图扫荡填写说明”">​</a></h3><ol><li><p>每条扫荡配置形如 <code>region</code>-<code>task number</code>-<code>sweep times</code></p><table tabindex="0"><thead><tr><th>名称</th><th>含义</th><th>限制</th></tr></thead><tbody><tr><td>region</td><td>区域</td><td>1-最新章节</td></tr><tr><td>task number</td><td>关卡</td><td>1-5</td></tr><tr><td>sweep times</td><td>扫荡次数</td><td>正整数/max</td></tr></tbody></table></li><li><p>每条配置都用<code>&quot;,&quot;</code> 隔开</p></li><li><p>特殊说明</p></li></ol><ul><li><code>sweep times</code> 可以为 <code>max</code></li><li>BAAS会根据当前体力和关卡体力计算当前关卡能否扫荡, 不足会直接退出。</li><li>遇到<strong>扫荡次数不足</strong>的关卡会<strong>跳过</strong>, 不会=<strong>购买更多扫荡次数</strong></li></ul><h3 id="各图所需属性" tabindex="-1">各图所需属性 <a class="header-anchor" href="#各图所需属性" aria-label="Permalink to “各图所需属性”">​</a></h3><table tabindex="0"><thead><tr><th>区域</th><th>属性</th></tr></thead><tbody><tr><td>28</td><td>爆发, 振动</td></tr><tr><td>27</td><td>贯穿, 爆发</td></tr><tr><td>26</td><td>神秘, 爆发</td></tr><tr><td>25</td><td>振动, 贯穿</td></tr><tr><td>24</td><td>振动, 爆发</td></tr><tr><td>23</td><td>爆发, 贯穿</td></tr><tr><td>22</td><td>贯穿, 神秘</td></tr><tr><td>21</td><td>神秘, 爆发</td></tr><tr><td>20</td><td>爆发, 贯穿</td></tr><tr><td>19</td><td>贯穿, 神秘</td></tr><tr><td>18</td><td>神秘, 爆发</td></tr><tr><td>17</td><td>爆发, 贯穿</td></tr><tr><td>16</td><td>贯穿, 神秘</td></tr><tr><td>15</td><td>神秘, 神秘</td></tr><tr><td>14</td><td>爆发, 神秘</td></tr><tr><td>13</td><td>贯穿, 贯穿</td></tr><tr><td>12</td><td>神秘, 爆发</td></tr><tr><td>11</td><td>贯穿, 神秘</td></tr><tr><td>10</td><td>爆发, 神秘</td></tr><tr><td>9</td><td>爆发, 贯穿</td></tr><tr><td>8</td><td>贯穿, 贯穿</td></tr><tr><td>7</td><td>爆发, 爆发</td></tr><tr><td>6</td><td>贯穿, 贯穿</td></tr><tr><td>5</td><td>爆发</td></tr><tr><td>4</td><td>贯穿</td></tr><tr><td>3</td><td>贯穿</td></tr><tr><td>2</td><td>爆发</td></tr><tr><td>1</td><td>爆发</td></tr></tbody></table><h3 id="活动图扫荡填写说明" tabindex="-1">活动图扫荡填写说明 <a class="header-anchor" href="#活动图扫荡填写说明" aria-label="Permalink to “活动图扫荡填写说明”">​</a></h3><ol><li><p>每条扫荡配置由 <code>task number</code> , <code>sweep times</code>构成, 所有扫荡关卡和次数分别用<code>&quot;,&quot;</code>。即扫荡关卡为<code>[关卡1, 关卡2, ...]</code>，扫荡次数为<code>[关卡1次数, 关卡2次数, ...]</code></p><table tabindex="0"><thead><tr><th>名称</th><th>含义</th><th>限制</th></tr></thead><tbody><tr><td>task number</td><td>关卡</td><td>1-12</td></tr><tr><td>sweep times</td><td>扫荡次数</td><td>正整数/小数/分数</td></tr></tbody></table></li><li><p>扫荡次数:</p></li></ol><ul><li>正整数 表示扫荡次数</li><li>小数 0.5 表示使用当前 体力*0.5 的 体力扫荡该关卡</li><li>分数 1/3 表示使用当前体力的1/3扫荡该关卡</li><li>当活动关卡扫荡次数设置为-1时，将可用体力平均分配至所选的多个关卡，并尽可能用尽所有体力。</li></ul><p>例:</p><ul><li>设体力: 999</li></ul><table tabindex="0"><thead><tr><th>扫荡关卡</th><th>扫荡次数</th><th>BAAS</th></tr></thead><tbody><tr><td>9,10,11</td><td>0.5,3,1/3</td><td>扫荡第9关25次, 第10关3次, 第11关16次</td></tr></tbody></table><p>计算:</p><ul><li>(999 * 0.5) / 20 = 25次</li><li>(999 * 1/3）/ 20 = 16次</li></ul><table tabindex="0"><thead><tr><th>扫荡关卡</th><th>扫荡次数</th><th>BAAS</th></tr></thead><tbody><tr><td>9,10,11</td><td>-1</td><td>分别扫荡第9, 10, 11关16次(均摊体力扫荡)</td></tr></tbody></table><p><strong>note:</strong> BA通常活动关扫荡一次体力消耗:</p><table tabindex="0"><thead><tr><th>关卡</th><th>体力消耗</th></tr></thead><tbody><tr><td>1 - 4</td><td>10</td></tr><tr><td>5 - 8</td><td>15</td></tr><tr><td>9 - 12</td><td>20</td></tr></tbody></table><h3 id="调度配置" tabindex="-1">调度配置 <a class="header-anchor" href="#调度配置" aria-label="Permalink to “调度配置”">​</a></h3><ol><li><p>优先级:当有多于两个任务在队列中时，会优先做优先级小的任务</p></li><li><p>执行间隔:整数 0 表示间隔一天，</p></li><li><p>每日重置:</p><ul><li><p>每天固定在这些时间做对应任务(可以有多个时间戳，<code>&quot;,&quot;</code>隔开) 填写格式 <code>[[h, m, s]]</code> （UTC时间） 例：</p><table tabindex="0"><thead><tr><th>每日重置</th><th>BAAS</th></tr></thead><tbody><tr><td><code>[[0, 0, 0], [20, 0, 0]]</code></td><td>北京时间(UTC + 8)8点,4点固定执行该任务</td></tr></tbody></table></li></ul></li><li><p>禁用时间段:</p><ul><li>在所有禁用时间段不做该任务 （可以有多个时间段,<code>&quot;,&quot;</code>隔开） 填写格式 <code>[[[h1, m1, s1], [h2, m2, s1]]]</code> (UTC时间) 例：</li></ul><table tabindex="0"><thead><tr><th>每日重置</th><th>BAAS</th></tr></thead><tbody><tr><td><code>[[[0, 0, 0], [24, 0, 0]]]</code></td><td>表示全天不做</td></tr></tbody></table></li><li><p>前置任务</p><ul><li>在执行该任务前依次执行前置任务里所有任务, 不会递归执行前置任务的前置任务</li></ul></li><li><p>后置任务</p><ul><li>在执行该任务后依次执行后置任务里所有任务</li></ul></li></ol><h3 id="推送设置" tabindex="-1">推送设置 <a class="header-anchor" href="#推送设置" aria-label="Permalink to “推送设置”">​</a></h3><ul><li><strong>serverchan：填写ServerChan提供的SendKey</strong></li><li><strong>json：填写自定义的完整地址（如<code>http://127.0.0.1:8081/</code>）</strong></li><li><strong>推送的json格式为:</strong><code>{&quot;title&quot;:&quot;Baas Error&quot;,&quot;desp&quot;:&quot;error...&quot;}</code></li></ul><h2 id="其他实用设置" tabindex="-1">其他实用设置 <a class="header-anchor" href="#其他实用设置" aria-label="Permalink to “其他实用设置”">​</a></h2><h3 id="尽可能多获取指定角色好感" tabindex="-1">尽可能多获取指定角色好感 <a class="header-anchor" href="#尽可能多获取指定角色好感" aria-label="Permalink to “尽可能多获取指定角色好感”">​</a></h3><ol><li>角色好感度获取方法:</li></ol><ul><li>咖啡厅摸头(保底一日8次) <a href="#如何设置baas咖啡厅一日摸8次指定角色">设置方式</a></li><li>做指定角色的日程 <a href="#如何设置baas做指定角色日程">设置方式</a></li><li>制造角色喜欢的礼物 <a href="#如何设置baas制造指定角色喜欢的礼物">设置方式</a></li></ul>',79)]))}const m=d(o,[["render",h]]);export{u as __pageData,m as default};
