import{_ as r,c as e,o as s,ah as a}from"./chunks/framework.DaDXF32t.js";const o="/assets/ui.Bd35evDi.png",i="/assets/ui2.zyD8boYZ.png",n="/assets/ui3.B3bAUQRd.png",f=JSON.parse('{"title":"Blue Archive Auto Script","description":"","frontmatter":{},"headers":[],"relativePath":"usage_doc/features.md","filePath":"usage_doc/features.md","lastUpdated":1757509302000}'),l={name:"usage_doc/features.md"};function g(p,t,c,u,h,_){return s(),e("div",null,t[0]||(t[0]=[a('<h1 id="blue-archive-auto-script" tabindex="-1">Blue Archive Auto Script <a class="header-anchor" href="#blue-archive-auto-script" aria-label="Permalink to “Blue Archive Auto Script”">​</a></h1><p><img src="https://img.shields.io/badge/-Python-000000?style=flat&amp;logo=python" alt="Python"></p><p><strong>BAAS</strong>, 一个带GUI的蔚蓝档案（全服支持），为屏幕分辨率为 16:9 (1280x720 最佳), 全年无休挂机运行的场景而设计 最终目的是接管一切蔚蓝档案任务 实现完全自动化</p><p>关于安装，您可以参考我们的演示视频：<a href="https://www.acfun.cn/v/ac47360708_2" target="_blank" rel="noreferrer">视频链接</a></p><p><strong>BAAS</strong> 是一款免费开源软件，地址：<a href="https://github.com/pur1fying/blue_archive_auto_script" target="_blank" rel="noreferrer">Github</a></p><p>GUI预览图： <img src="'+o+'" alt="gui"></p><p><img src="'+i+'" alt="gui"></p><p><img src="'+n+'" alt="gui"></p><h2 id="功能-features" tabindex="-1">功能 Features <a class="header-anchor" href="#功能-features" aria-label="Permalink to “功能 Features”">​</a></h2><ul><li><strong>角色好感度</strong>: 咖啡厅一日8摸, 日程自动找指定学生, 每日最大限度提升好感度</li><li><strong>主线</strong>: 自动推图(普通4-28，困难1-28，最新主线&lt;28&gt;已适配)</li><li><strong>咖啡厅</strong>: 邀请券可选择指定学生 咖啡厅摸头 咖啡厅奖励</li><li><strong>商店</strong>: 支持指定普通物品商店 以及竞技场商店自动购买和刷新次数</li><li><strong>收获</strong>: 每日小组体力 邮箱 竞技场每日领奖 总力战累计积分领奖 每日任务领奖</li><li><strong>体力清理</strong>: 可指定任意主线关卡(普通困难) 特别委托 活动关卡 扫荡任意次数</li><li><strong>日程</strong>: 优先做指定角色存在的日程, 可优先做加好感度多的日程, 指定每个区域日程次数</li><li><strong>竞技场</strong>: 清理到没有竞技场挑战券为止，自动领取每日奖励</li><li><strong>制造</strong>: 无缝制造三解 指定制造优先级 次数 是否使用加速券</li><li><strong>momotalk</strong>: 自动完成所有未结束对话 完成剧情 领取青辉石</li><li><strong>总力战</strong>: 清空总力战挑战券并领取奖励(自动凹分功已在<a href="https://github.com/pur1fying/BAAS_Cpp" target="_blank" rel="noreferrer">该仓库</a>适配, 正在接入<strong>BAAS</strong>)</li><li><strong>战术综合测试</strong>: 在考试开启期间自动清票</li><li><strong>剧情</strong>: 一键清理主线剧情，小组剧情, 迷你剧情</li><li><strong>活动</strong>: 一键活动推剧情，任务，挑战，走格子(三服活动稳定更新)</li></ul><h2 id="突出特性" tabindex="-1">突出特性 <a class="header-anchor" href="#突出特性" aria-label="Permalink to “突出特性”">​</a></h2><ul><li><strong>16:9分辨率模拟器都可以运行，不局限于1280x720</strong></li><li><strong>在低配电脑上运行也不会出现问题</strong> 处理器速度低的电脑可以手动调小截图速度 增长运行时间</li></ul>',12)]))}const m=r(l,[["render",g]]);export{f as __pageData,m as default};
