import{_ as t,c as l,a2 as i,o}from"./chunks/framework.By2iWbs8.js";const a="/blue_archive_auto_script/assets/problem_fail_to_start_ui_for_path_with_chinese.Bj8ctvAY.png",s="/blue_archive_auto_script/assets/add_QT_plugins_into_PATH.yUlqmqXj.png",g=JSON.parse('{"title":"在Windows操作系统安装BAAS","description":"","frontmatter":{},"headers":[],"relativePath":"usage_doc/install/Windows.md","filePath":"usage_doc/install/Windows.md","lastUpdated":1739450142000}'),r={name:"usage_doc/install/Windows.md"};function c(n,e,u,d,p,_){return o(),l("div",null,e[0]||(e[0]=[i('<h1 id="在windows操作系统安装baas" tabindex="-1">在Windows操作系统安装BAAS <a class="header-anchor" href="#在windows操作系统安装baas" aria-label="Permalink to &quot;在Windows操作系统安装BAAS&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>还没下载？来<a href="./../downloads.html">这里</a></p></div><h2 id="安装步骤" tabindex="-1">安装步骤 <a class="header-anchor" href="#安装步骤" aria-label="Permalink to &quot;安装步骤&quot;">​</a></h2><ul><li>解压Release或qq交流群中的下载包，双击<code>BlueArchiveAutoScript.exe</code>安装环境，请耐心等待。 安装完成后，BAAS 的ui界面将自动启动。</li><li><a href="https://www.bilibili.com/video/BV19y421e7XF/?spm_id_from=333.337.search-card.all.click" target="_blank" rel="noreferrer">视频教程</a></li></ul><h2 id="问题解答" tabindex="-1">问题解答 <a class="header-anchor" href="#问题解答" aria-label="Permalink to &quot;问题解答&quot;">​</a></h2><h3 id="_1-确保安装路径没有中文" tabindex="-1">1.确保安装路径没有中文 <a class="header-anchor" href="#_1-确保安装路径没有中文" aria-label="Permalink to &quot;1.确保安装路径没有中文&quot;">​</a></h3><ul><li><p><strong>报错截图</strong><img src="'+a+'" alt="中文路径无法启动ui"></p></li><li><p><strong>原因:</strong></p><ul><li>UI使用QT框架的限制</li></ul></li><li><p><strong>解决方法</strong></p><ul><li>方法1. 将BAAS安装路径更改为不含中文的路径</li><li>方法2. 将本应该在安装时添加的环境变量添加回去就好了 <ul><li><strong>具体操作环节为</strong> <code>windows设置→系统→关于→高级系统设置→高级→环境变量→新建→填写变量名与变量值→点两下确定关闭窗口</code><img src="'+s+'" alt="solve2"></li><li><strong>变量名:</strong> <code>QT_QPA_PLATFORM_PLUGIN_PATH</code></li><li><strong>变量值:</strong> 填写已知兼容的Qt路径 ↓ <ol><li>Baas的Qt目录： <code>（Baas的安装路径）\\BlueArchiveAutoSctipt\\env\\Lib\\site-packages\\PyQt5\\Qt5\\plugins</code></li><li>MuMu模拟器的Qt目录： <code>（MuMu模拟器安装路径）\\MuMu Player 12\\shell\\plugins</code></li><li>自己装的Qt： 自己研究，在py的安装目录下或者在AppData\\Local的py软件配置文件夹里</li></ol></li></ul></li></ul><p><strong>note:</strong> 变量值填写非MuMu模拟器的Qt目录时可能会与MuMu模拟器所需的Qt发生冲突，导致MuMu模拟器无法打开，已知MUMU模拟器与最新的Qt5.15.11不兼容，非必要不建议填写自己安装的Qt（要用到时自己改）。 <strong>因为MUMU模拟器的Qt与Baas兼容，所以在发生冲突无法打开MuMu模拟器时建议在变量值处填写</strong> <code>MuMu模拟器的Qt目录</code> 删除变量也可以正常打开MuMu模拟器。</p></li></ul><h3 id="_2-双击bluearchiveautoscript-exe后命令行界面全黑无反应" tabindex="-1">2.双击BlueArchiveAutoScript.exe后命令行界面全黑无反应 <a class="header-anchor" href="#_2-双击bluearchiveautoscript-exe后命令行界面全黑无反应" aria-label="Permalink to &quot;2.双击BlueArchiveAutoScript.exe后命令行界面全黑无反应&quot;">​</a></h3><ul><li><strong>解决方法</strong></li></ul><ol><li>打开windows设置</li><li>搜索并进入<code>病毒和威胁防护</code></li><li>点击<code>&quot;病毒和威胁防护&quot;设置</code>栏下的<code>管理设置</code></li><li>关闭<code>实时保护</code></li></ol><ul><li><strong>原因:</strong> 未知, 可能windows误识为病毒</li></ul>',11)]))}const m=t(r,[["render",c]]);export{g as __pageData,m as default};
