import{_ as t,c as e,o as i,ah as a}from"./chunks/framework.DaDXF32t.js";const o="/assets/problem_fail_to_start_ui_for_path_with_chinese.Bj8ctvAY.png",s="/assets/add_QT_plugins_into_PATH.yUlqmqXj.png",r="/assets/install_request400.qQrmbyRT.png",n="/assets/setup_toml_REPO_URL_HTTP.C4xzzg1D.png",A=JSON.parse('{"title":"在Windows操作系统安装BAAS","description":"","frontmatter":{},"headers":[],"relativePath":"usage_doc/install/Windows.md","filePath":"usage_doc/install/Windows.md","lastUpdated":1750095118000}'),c={name:"usage_doc/install/Windows.md"};function u(d,l,_,p,g,h){return i(),e("div",null,l[0]||(l[0]=[a('<h1 id="在windows操作系统安装baas" tabindex="-1">在Windows操作系统安装BAAS <a class="header-anchor" href="#在windows操作系统安装baas" aria-label="Permalink to “在Windows操作系统安装BAAS”">​</a></h1><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>还没下载？来<a href="./../downloads.html">这里</a></p></div><h2 id="安装步骤" tabindex="-1">安装步骤 <a class="header-anchor" href="#安装步骤" aria-label="Permalink to “安装步骤”">​</a></h2><ul><li>解压Release或qq交流群中的下载包，双击<code>BlueArchiveAutoScript.exe</code>安装环境，请耐心等待。 安装完成后，BAAS 的ui界面将自动启动。</li><li><a href="https://www.acfun.cn/v/ac47360708_2" target="_blank" rel="noreferrer">视频教程</a></li></ul><h2 id="问题解答" tabindex="-1">问题解答 <a class="header-anchor" href="#问题解答" aria-label="Permalink to “问题解答”">​</a></h2><h3 id="确保安装路径没有中文" tabindex="-1">确保安装路径没有中文 <a class="header-anchor" href="#确保安装路径没有中文" aria-label="Permalink to “确保安装路径没有中文”">​</a></h3><ul><li><p><strong>报错截图</strong><img src="'+o+'" alt="中文路径无法启动ui"></p></li><li><p><strong>原因:</strong></p><ul><li>UI使用QT框架的限制</li></ul></li><li><p><strong>解决方法</strong></p><ul><li>方法1. 将BAAS安装路径更改为不含中文的路径</li><li>方法2. 将本应该在安装时添加的环境变量添加回去就好了 <ul><li><strong>具体操作环节为</strong> <code>windows设置→系统→关于→高级系统设置→高级→环境变量→新建→填写变量名与变量值→点两下确定关闭窗口</code><img src="'+s+'" alt="solve2"></li><li><strong>变量名:</strong> <code>QT_QPA_PLATFORM_PLUGIN_PATH</code></li><li><strong>变量值:</strong> 填写已知兼容的Qt路径 ↓ <ol><li>Baas的Qt目录： <code>（Baas的安装路径）\\BlueArchiveAutoSctipt\\env\\Lib\\site-packages\\PyQt5\\Qt5\\plugins</code></li><li>MuMu模拟器的Qt目录： <code>（MuMu模拟器安装路径）\\MuMu Player 12\\shell\\plugins</code></li><li>自己装的Qt： 自己研究，在py的安装目录下或者在AppData\\Local的py软件配置文件夹里</li></ol></li></ul></li></ul><p><strong>note:</strong> 变量值填写非MuMu模拟器的Qt目录时可能会与MuMu模拟器所需的Qt发生冲突，导致MuMu模拟器无法打开，已知MUMU模拟器与最新的Qt5.15.11不兼容，非必要不建议填写自己安装的Qt（要用到时自己改）。 <strong>因为MUMU模拟器的Qt与Baas兼容，所以在发生冲突无法打开MuMu模拟器时建议在变量值处填写</strong> <code>MuMu模拟器的Qt目录</code> 删除变量也可以正常打开MuMu模拟器。</p></li></ul><h3 id="双击bluearchiveautoscript-exe后命令行界面全黑无反应" tabindex="-1">双击BlueArchiveAutoScript.exe后命令行界面全黑无反应 <a class="header-anchor" href="#双击bluearchiveautoscript-exe后命令行界面全黑无反应" aria-label="Permalink to “双击BlueArchiveAutoScript.exe后命令行界面全黑无反应”">​</a></h3><ul><li><strong>解决方法</strong></li></ul><ol><li>打开windows设置</li><li>搜索并进入<code>病毒和威胁防护</code></li><li>点击<code>&quot;病毒和威胁防护&quot;设置</code>栏下的<code>管理设置</code></li><li>关闭<code>实时保护</code></li></ol><ul><li><strong>原因:</strong> 未知, 可能windows误识为病毒</li></ul><h3 id="安装命令行出现如下报错" tabindex="-1">安装命令行出现如下报错 <a class="header-anchor" href="#安装命令行出现如下报错" aria-label="Permalink to “安装命令行出现如下报错”">​</a></h3><p><img src="'+r+'" alt="install_request400.png"></p><ul><li><strong>解决方法</strong></li></ul><ol><li>打开<strong>BAAS</strong>安装目录下的setup.toml</li><li>尝试修改<a href="/usage_doc/install/setup_config.html#repo-url-http">REPO_URL_HTTP</a>为其他地址 (gitcode / github) <img src="'+n+'" alt="setup_toml_REPO_URL_HTTP.png"></li></ol><ul><li><strong>原因:</strong> 你可能被gitee拒绝服务</li></ul>',16)]))}const b=t(c,[["render",u]]);export{A as __pageData,b as default};
